import React, { useState, useContext, useEffect } from "react";
import DataContext from "../contexts/dataContext";
import { TaskBar, List } from "@react95/core";
import styled from "styled-components";
import { startWebamp } from "../utils/startWebamp";
import Notepad from "./Notepad";

const Link = styled.a`
  text-decoration: none;
  color: inherit;
`;

function Taskbar() {
  const openNotepad = (item) => {
    setSelectedItem(item);
    toggleNotepad(true);
  };
  const [notepadOpened, toggleNotepad] = useState(false);
  const { projectRepo, react95Repo } = useContext(DataContext).getProjectInfo();
  console.log(projectRepo, react95Repo);
  return (
    <TaskBar
      list={
        <List>
          <List.Item className="pointer" icon="media_cd">
            <Link onClick={() => startWebamp()} target="_blank">
              Spela
            </Link>
          </List.Item>
          <List.Divider />
          <List.Item className="pointer" icon="folder_file">
            <Link onClick={() => openNotepad(item)} target="_blank">
              Repo
            </Link>
          </List.Item>
        </List>
      }
    />
  );
}

export default Taskbar;
